CREATE TABLE  `users` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT ,`username` VARCHAR( 100 ) NOT NULL ,`email` VARCHAR( 100 ) NOT NULL ,`password` VARCHAR( 100 ) ,`secret` VARCHAR( 100 ) ,`active` TINYINT( 1 ) UNSIGNED DEFAULT  '0' NOT NULL ,PRIMARY KEY (  `id` ) ,UNIQUE (`username`,`email`)) TYPE = INNODB;
