CREATE TABLE users (id int(10) unsigned NOT NULL AUTO_INCREMENT,username varchar(100) COLLATE latin1_general_ci NOT NULL,email varchar(100) COLLATE latin1_general_ci NOT NULL,`password` varchar(100) COLLATE latin1_general_ci DEFAULT NULL,secret varchar(100) COLLATE latin1_general_ci DEFAULT NULL,active tinyint(1) unsigned NOT NULL DEFAULT '0',PRIMARY KEY (id),UNIQUE KEY username (username,email)) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci AUTO_INCREMENT=1;